package 확인문제;

import javax.swing.JOptionPane;

public class Q2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 2번문제
		int num1 = 30;
		int num2 = 40;
		
		System.out.println("두 수를 더한 값은 " + (num1 + num2) + "입니다.");
		System.out.println("num1에서 num2를 나눈 값은 " + (num1 / num2) + "입니다.");
		System.out.println("num1에서 num2를 나눈 나머지 값은 " + (num1 % num2) + "입니다.");
	}

}
